	program convso41C convert CTD-data of cruise Sonne 41 into RO-standard format	character*80	inline, outfile, dummy1	character	inl(80), outf(80), oldl(80), dummy2(5)	real		data(2000,3)	integer		count, filenr	equivalence	(inline,inl), (outfile,outf)	open(unit=10,file='ctdso41.dat')	kont=1	count=0	outfile='so41_000.7'	filenr=0 	first=1	do while (kont.eq.1)	  read(10,'(80a)',end=10)inline	  if (first.eq.1) then	    do k=1,80	      oldl(k)=inl(k)	    enddo	    first=0	  endif	  if (inl(1).eq.'S') then	    print*,inline	    print*,count	    if (filenr.gt.9) then	      outf(7)=char( int(filenr/10.) + 48)	      outf(8)=char( filenr-int(filenr/10.)*10 + 48)	    else	      outf(8)=char( filenr + 48)	    endif	    if (filenr.gt.0) then	      open(unit=11,file=outfile)	      write(11,'(a)')'Filename       = ctdso41.dat'	      write(11,'(a)')'Cruise         = Sonne 41'	      write(11,'(a,5a)')'Station        = ',(oldl(nnn),nnn=14,18)	      write(11,'(a,8a)')'Date           = 19',oldl(32),oldl(33),'/',oldl(29),oldl(30),'/',oldl(26),oldl(27)	      write(11,'(a,5a)')'Start_Time     = ',(oldl(nnn),nnn=42,46)	      write(11,'(a,8a)')'Start_Lat      = ',(oldl(nnn),nnn=53,60)	      write(11,'(a,9a)')'Start_Lon      = ',(oldl(nnn),nnn=62,70)	      write(11,'(a)')'Columns        = P:T:S'	      do n=1,count	 	write(11,'(f6.1,2f7.3)')data(n,1),data(n,2),data(n,3)	      enddo	      close(11)	    endif	    count=0	    filenr=filenr+1	    do k=1,80	      oldl(k)=inl(k)	    enddo	  else	    count=count+1	    read(inline,*,end=10)d1,d2,d3,d4	    data(count,1)=d1	    data(count,2)=d2	    data(count,3)=d4	  endif	  goto 2010	  continue	  kont=020	  continue	enddo	close(10)	    if (filenr.gt.9) then	      outf(7)=char( int(filenr/10.) + 48)	      outf(8)=char( filenr-int(filenr/10.)*10 + 48)	    else	      outf(8)=char( filenr + 48)	    endif	      open(unit=11,file=outfile)	      write(11,'(a)')'Filename       = ctdso41.dat'	      write(11,'(a)')'Cruise         = Sonne 41'	      write(11,'(a,5a)')'Station        = ',(oldl(nnn),nnn=14,18)	      write(11,'(a,8a)')'Date           = 19',oldl(32),oldl(33),'/',oldl(29),oldl(30),'/',oldl(26),oldl(27)	      write(11,'(a,5a)')'Start_Time     = ',(oldl(nnn),nnn=42,46)	      write(11,'(a,8a)')'Start_Lat      = ',(oldl(nnn),nnn=53,60)	      write(11,'(a,9a)')'Start_Lon      = ',(oldl(nnn),nnn=62,70)	      write(11,'(a)')'Columns        = P:T:S'	      do n=1,count	 	write(11,'(f6.1,2f7.3)')data(n,1),data(n,2),data(n,3)	      enddo	      close(11)	end