function [shortcut,fileinfo]=rodblookup(arg1,arg2,arg3,arg4,arg5,arg6,arg7,...	arg8,arg9,arg10,arg11,arg12);%function [shortcut,fileinfo]=rodblookup(arg1,arg2,arg3,arg4,arg5,arg6,arg7,...%	arg8,arg9,arg10,arg11,arg12);%% lookup RODB database for existing files%% input  :	arg'n'		: one of 'lat','lon','date','time','cruise',%					 'system','%% output :	shortcut	: array of filename shortcuts for 'rodbload'%		fileinfo	: array of informations about file%% date and time are not yet implemented !!!!!!!!%% version	0.1.2		last change 11.06.1997% load database%eval('load ',rodbpath,'/rodb_info'])load rodb_info% display date of database informationdisp(' ')disp(['last update of database on : ',rodb_date])disp(' ')% define 'good' filesgood=[1:size(rodb_data,1)];% get arguments and find relevant profilesi=1;while i<nargin  arg=eval(['arg',int2str(i)]);  rge=eval(['arg',int2str(i+1)]);% search for latitude  if strcmp(arg,'lat')    good=good(find( (rodb_data(good,6)>=rge(1)) &...	 (rodb_data(good,6)<=rge(2)) ));  end% search for longitude  if strcmp(arg,'lon')    good=good(find( (rodb_data(good,7)>=rge(1)) &...	 (rodb_data(good,7)<=rge(2)) ));  end% search for cruise  if strcmp(arg,'cruise')    rg2=rge;    idx=[];    while ~isempty(rg2)      [rg1,rg2]=strtok(rg2,':');      dummy=rodb_file(good,:)';      dummy=findstr(rg1,dummy(:)');      [a,dummy]=mod(dummy,size(rodb_file,2));      idx=[idx,dummy+1];    end    good=good(idx);  end% search for system  if strcmp(arg,'system')    dummy=rodb_file(good,:)';    dummy=findstr(rge,dummy(:)');    [a,dummy]=mod(dummy,size(rodb_file,2));    good=good(dummy+1);  end  i=i+2;end% extract resultsshortcut=rodb_file(good,:);fileinfo=rodb_data(good,:);