function p = rodbpath(p1,p2)%RODBPATH Control RO database search path.%  RODBPATH, by itself, prettyprints the current search path for%  RO database files.%  P = RODBPATH returns a string containing the path in p.  %  RODBPATH(P) changes the path to p.%  RODBPATH(P1,P2) changes the path to the concatenation of the%  two path strings p1 and p2. Thus RODBPATH(RODBPATH,P) appends%  a new directory to the current path and RODBPATH(P,RODBPATH)%  prepends a new path.%  C. Mertens, IfM Kiel%  $Revision: 1.3 $ $Date: 1996/12/17 11:06:09 $% path seperatord = ':';% get home directoryhome = getenv('HOME');if exist([home,'/matlab/rodb.mat']) == 2  eval(['load ',home,'/matlab/rodb.mat'])else  dbpath = '';endif nargout == 0  if nargin == 0    fprintf('\n\t\tRODBPATH\n\n')    while length(dbpath) > 0      [directory,dbpath] = strtok(dbpath,d);      fprintf('\t%s\n',directory)    end  elseif nargin == 1    dbpath = p1;    eval(['save ',home,'/matlab/rodb.mat dbpath'])  elseif nargin == 2    dbpath = [p1,':',p2];    eval(['save ',home,'/matlab/rodb.mat dbpath'])  endelse  p = dbpath;end