function [P,T,C,S,jd,meas,sampling_rate,qflag] = load_seagauge(file,log);% function [P,T,C,S,jd,meas,sampling_rate,qflag] = load_seagauge(file,log);%% uses julian hms2h slsh = '/'; cln = ':';% ------ load data ------------filefid = fopen(file,'r');disp('load_seagauge.m: loading data')zeile = fscanf(fid,'%c');  %read data into stringdisp('complete')fclose(fid);% ------- convert data ---------nonum = findstr(zeile,slsh); zeile(nonum) = ' ' ;nonum = findstr(zeile,cln);zeile(nonum) = ' ' ;data = str2num(zeile);[m,n] = size(data);jd   = julian([data(:,[4 2 3]) hms2h(data(:,[5 6 7]))]);P    = data(:,8)/1.4503774;  % psi to dbar conversion;T    = data(:,9);meas = data(:,1);if n == 9   % no conductivity C = NaN; S = NaN;elseif n>9 C = data(:,10); S = data(:,11);endsampling_rate = round(1/median(diff(jd))); qflag = [];     