% function data = microcat_month(data)% convert microcat month string into month number% kanzowfunction data = microcat_month2(data)mon = ['Jan';'Feb';'Mar';'Apr';'May';'Jun';'Jul';'Aug';'Sep';'Oct';'Nov';'Dec'];II = [];for i = 1 :12,  ii = findstr(mon(i,:),data);  inp = sprintf('%3.3d',i);     data(ii)   = inp(1);  data(ii+1) = inp(2);  data(ii+2) = inp(3);  end  