% MC_CALL_2_OC459 is a script that performs stage1 processing% on microcat data.  It converts microcat data from raw to rodb% format for an entire mooring.%% It calls microcat2rodb_3 (to convert microcat_data), rodbload.m,% timeaxis.m, auto_filt.m, julian.m % 27/10/09 DR added functionality for .cnv files% 21/03/10 ZBS made all paths rely on a basedir variable,%   added a descriptive header, and modified for oceanus 459close allclear all %startup% -----------------------------------------------------------------% --- This is the information that needs to be modified for -------% --- different users, directory trees, and moorings --------------% -----------------------------------------------------------------basedir  = '/noc/ooc/rpdmoc/rapid/data/'; % at NOCS%basedir = '/Users/hydrosea5/kn200-4/rpdmoc/rapid/data/'% oceanus 459% determine whether this script is being run on Brian King's% computer (dhcp108), or else remotely by a computer that has% mounted the surman directory.[out,host] = unix('hostname');if strfind(host,'dhcp108.science.oceanus.whoi.edu');%  basedir  = '/Users/surman/rpdmoc/rapid/data/';else  %basedir  = '/Volumes/surman/rpdmoc/rapid/data/';%  basedir  = '/noc/ooc/rpdmoc/rapid/data/';end%cruise   = 'oc459';cruise = 'kn200_4';operator = 'efw';%moor = 'wb1_7_201008';%moor = 'wb2_8_201003';%moor = 'wbh2_4_201004';%moor = 'wb4_7_201026';%moor = 'wb6_4_201001';moor='mochae_5_392';%moor='mochab_5_391';% -----------------------------------------------------------------% --- set paths for data input and output ---addpath(genpath([basedir 'exec/' cruise '/']));%addpath(genpath([basedir 'moor']));inpath   = [basedir 'moor/raw/' cruise '/microcat/'];outpath  = [basedir 'moor/proc/' moor '/microcat/'];infofile = [basedir 'moor/proc/' moor '/' moor 'info.dat'];out_ext  = '.raw';% --- get mooring information from infofile ---[id,sn,lat,lon] = rodbload(infofile,'id:sn:latitude:longitude');if isempty(id) | isnan(id)  [id,sn,lat,lon] = ...      rodbload(infofile,'instrument:serialnumber:latitude:longitude');end% --- vector of serial numbers ---ii = find(id >= 333 & id <=337);vec = sn(ii);% --- write header info to log file ---fidlog = fopen([outpath,'stage1_log'],'w');fprintf(fidlog,'Transformation of ascii data to rodb format \n');fprintf(fidlog,'Processing carried out by %s at %s\n\n\n',operator,datestr(clock));fprintf(fidlog,'Mooring   %s \n',moor);fprintf(fidlog,'Latitude  %6.3f \n',lat);fprintf(fidlog,'Longitude %6.3f \n',lon);% --- loop through each instrument on the mooring ---for i = 1:length(vec),  fprintf(fidlog,'\n\n');  disp(['processing microcat serial number ' num2str(vec(i)) ])    infile = [inpath,sprintf('%4.4d',vec(i)),'rec2.asc'];  if exist(infile) ~= 2    infile = [inpath,sprintf('%4.4d',vec(i)),'rec.asc'];  end  if exist(infile) ~= 2    infile = [inpath,sprintf('%3.3d',vec(i)),'rec.asc'];  end  if exist(infile) ~= 2    infile = [inpath,sprintf('%4.4d',vec(i)),'REC.asc'];  end  if exist(infile) ~= 2    infile = [inpath,sprintf('%3.3d',vec(i)),'REC.asc'];  end  if exist(infile) ~= 2    infile = [inpath,'data',sprintf('%4.4d',vec(i)),'_2.asc'];  end  if exist(infile) ~= 2    infile = [inpath,'data',sprintf('%4.4d',vec(i)),'.asc'];  end  if exist(infile) ~= 2    infile = [inpath,sprintf('%4.4d',vec(i)),'.asc'];  end  if exist(infile) ~= 2    infile = [inpath,sprintf('%4.4d',vec(i)),'_data.cnv'];  end  if exist(infile) ~= 2    infile = [inpath,sprintf('%4.4d',vec(i)),'_data.asc'];  end  outfile = [outpath,moor,'_',sprintf('%4.4d',vec(i)),out_ext];  % --- convert from raw to rodb format ---  microcat2rodb_3(infile,outfile,infofile,fidlog,'y',0); % DR manually added 0 toffset value on D324.  %disp(['proceeding to next file '])   %pause  endcomment = input('Enter additional comment to be save in Log file: ','s'); if ~isempty(comment)  fprintf(fidlog,'\n COMMENT:\n %s',comment);endfclose(fidlog);