% SEAGAUGE_PROCESSING_003 is a script that performs stage2% processing on seagauge data from one mooring.  This consists of% adding a linear clock drift (if any) and determining what% functional form to use for the pressure drift.%% It calls purge_bp_003.m, rodbload.m, rodbsave.m, julian.m,% gregorian.m, auto_filt.m% 16.03.05 Kanzow% 06.11.08 Szuts% 27.03.10 ZB Szuts: modify for oc459, add descriptive headers,%   clean up a bit% 2011 Apr 13 EFW% 2012 Feb 15 SKYE% 09/05/14 DR tweaked "coff" value writing to log file to prevent numerous%          lines in file% 2016 July 6 SA02SC : Added linkaxes to plots for joint zooming of% subplotsclose allglobal MOORPROC_Gclearvars -except MOORPROC_Gmoor = input('mooring deployment (e.g. wb2l14_14_2020) to process:    ','s');pd = moor_inoutpaths('bpr',moor);% ZB Szuts, 27.03.10, oc459% There are 2 clock offset files for BPR instruments:% clock_offset.dat (in raw/oc459/) and BPR_clock_offset.dat in% (raw/oc459/seagauge/):% - clock_offset.dat corrects for constant day/year offsets that% arise from incorrect instrument setup at the beginning of the% record.% - bpr_clock_offset.dat corrects for smaller offsets measured upon% recovery in stage2 processing, and are applied as linear trends% for the whole record.sg_code       = 465;  % seagauge identifierdum           = -9999; % dummy value% --- get mooring information from infofile ---[lat,lon,wd,sdate,stime,edate,etime,z,type,serial]= ...    rodbload(pd.infofile,...             ['Latitude:Longitude:WaterDepth:StartDate:StartTime:'...              'EndDate:EndTime:z:instrument:serialnumber']);% --- find all the seagauges on the mooring ---sgI          = find(type==sg_code);sn  = serial(sgI);% --- write header info to log file ---fidlog  = fopen(pd.stage2log,'w');fprintf(fidlog,['Stage 2 processing of SBE26 \n data from '...                'mooring %s \n Date: %s \n'],moor,datestr(clock)');fprintf(fidlog,'Operator: %s\n',MOORPROC_G.operator');fprintf(fidlog,'Programme: %s\n',mfilename);% --- loop through each instrument on the mooring ---for ins = 1 : length(sgI)  serialnumber = sn(ins);  instrdepth   = z(sgI(ins));  disp(['SBE-26 instrument ',num2str(serialnumber),' has been found'])  pd.stage1file = fullfile(pd.stage1path,sprintf(pd.stage1form,serialnumber));  pd.stage2file = fullfile(pd.stage1path,sprintf(pd.stage2form,serialnumber));    % --- write to log file ---  fprintf(fidlog,'Serialnumber %d \n',serialnumber);  fprintf(fidlog,'Target Depth [m]: %4.4d\n',instrdepth);  fprintf(fidlog,'Infile %s \n',pd.stage1file);  fprintf(fidlog,'Outfile %s \n',pd.stage2file);  % --- load data and carry out basic checks ---  [yy,mm,dd,hh,P,T] = rodbload(pd.stage1file,'YY:MM:DD:HH:P:T');  y0 = strsplit(moor,'_'); y0 = str2double(y0{end});  t0 = datenum(y0,1,1);  yd = datenum(yy,mm,dd,hh,zeros(size(hh)),zeros(size(hh)))-t0;  % ------- check data ----------------  sampling_rate = round(1./median(diff(yd))); % nominal sampling rate [per day]   % ---- check for a time offset entry in the 'clock_file' -----  time_corr = input('Shall time be corrected for clock offset? y/n ','s');  if strcmp(time_corr,'y')      load(pd.offsetfile)    ins = find(serialnumber == bpr_clock_offset(:,2) & ...               sg_code      == bpr_clock_offset(:,1));    if isempty(ins)      fprintf(1,'Clock offset not found in %s',pd.offsetfile);      fprintf(fidlog,'Clock offset not found in %s',pd.offsetfile);      coff = input('Enter post deployment clock offset [s] from log sheet ');    else       coff = bpr_clock_offset(ins,3);    end    % --- calculate a linear clock drift and subtract it from yd ---    len  = length(yd);    coff2 = linspace(0,coff/86400,len); % assume linear clock drift    yd   = yd - coff2';  % subtract drift        fprintf(1,'Clock offset at recovery of %d s (relative to UTC)\n',coff)    fprintf(1,['Calculated a linear trend going through '...               'yd([1 end]) and [0 %s]\n'],coff);    fprintf(1,'and subtracted it from the recorded time')    fprintf(fidlog,'Clock offset at recovery of %d s (relative to UTC)\n',coff)    fprintf(fidlog,['Calculated a linear trend going through '...                    'yd([1 end]) and [0 %s]\n'],coff);    fprintf(fidlog,'and subtracted it from the recorded time')      end  yd_start = datenum([sdate' stime' 0])-t0;  yd_end   = datenum([edate' etime' 0])-t0;  % --- calculate fits for pressure drifts ---  [pr_int,pr_exfit,t_int,yd_grid,pr_linfit,coef_exfit,coef_linfit] =  ...       purge_bp_003(sampling_rate,yd,P,T,[yd_start yd_end],fidlog);  % ------------ graphics ------------------------  yd0 = datenum(-1,1,1,24)-t0;  yd1 = yd_start-yd0;  yd2 = yd_end-yd0;  % --- plot original data ---  figure(21);   clf  [p1]=subplot(2,1,1);   % SKYE   ii = find(~isnan(pr_int) & pr_int~=dum);  plot(yd_grid(ii)-yd0,pr_int(ii))  title(['Seagauge s/n: ',num2str(serialnumber),...         '; Target Depth: ',num2str(instrdepth)])  ylabel('Pressure [dbar]')  grid on  xlim([yd1 yd2])  %timeaxis(plot_interval(1,1:3));     datetick('x',12)  [p2]=subplot(2,1,2); ii = find(~isnan(t_int) & t_int~=dum);  plot(yd_grid(ii)-yd0,t_int(ii))  ylabel('Temperature [deg C]')  grid on  xlim([yd1 yd2])  %   timeaxis(plot_interval(1,1:3));     orient tall  datetick('x',12)  linkaxes([p1,p2],'x');  eval(['print -depsc ',outfile,'.1.eps'])     % --- plot filtered data ---  figure(22);clf     % 2 day low pass  [p1]=subplot(3,1,1); ii = find(~isnan(pr_int) & pr_int~=dum);  plot(yd_grid(ii)-yd0,auto_filt(pr_int(ii),sampling_rate,1/2,'low',4))  hold on, grid on  plot(yd_grid-yd0,pr_exfit,'r')  plot(yd_grid-yd0,pr_linfit,'g')  legend('data','exp-lin fit','lin fit')  title(['Seagauge s/n: ',num2str(serialnumber),...         '; Target Depth: ',num2str(instrdepth),' 2-day low pass'])  ylabel('Pressure [dbar]')  xlim([yd1 yd2])  %    timeaxis(plot_interval(1,1:3));     datetick('x',12)  % --- decide whether an exponential+linear or ---  % --- linear fit is more apropriate ---  disp('Which empirical fit should be stored (see figure 22):')  fit_select = input('  (1) exponential-linear  or  (2) linear ? ');    if fit_select == 1    fprintf(fidlog,'Exponential-linear pressure fit to be stored with data \n');    fprintf(fidlog,'  amplitude of exponential decay: %f db \n',coef_exfit(1));    fprintf(fidlog,'  time-constant of exponential decay: %f days \n',1/coef_exfit(2));    fprintf(fidlog,'  linear slope: %f db/day \n',coef_exfit(3));    fprintf(fidlog,'  mean offset: %f db \n',coef_exfit(4));        fprintf(1,'Exponential-linear pressure fit to be stored with data \n');    fprintf(1,'  amplitude of exponential decay: %f db \n',coef_exfit(1));    fprintf(1,'  time-constant of exponential decay: %f days \n',1/coef_exfit(2));    fprintf(1,'  linear slope: %f db/day \n',coef_exfit(3));    fprintf(1,'  mean offset: %f db \n',coef_exfit(4));    pfit = pr_exfit;  elseif fit_select == 2    fprintf(fidlog,'Linear pressure fit to be stored with data \n');    fprintf(fidlog,'  linear slope: %f db/day \n',coef_linfit(1));    fprintf(fidlog,'  mean offset: %f db \n',coef_linfit(2));    fprintf(1,'Linear pressure fit to be stored with data \n');    fprintf(1,'  linear slope: %f db/day \n',coef_linfit(1));    fprintf(1,'  mean offset: %f db \n',coef_linfit(2));    pfit = pr_linfit;  end   [p2]=subplot(3,1,2);   plot(yd_grid(ii)-yd0,auto_filt(pr_int(ii),sampling_rate,1/2,'low',4)-pfit)  title('Empirical drift estimate subtracted')   ylabel('Pressure [dbar]')  grid on  xlim([yd1 yd2])  %    timeaxis(plot_interval(1,1:3));     datetick('x',12)  [p3]=subplot(3,1,3); ii = find(~isnan(t_int) & t_int~=dum);  plot(yd_grid(ii)-yd0,auto_filt(t_int(ii),sampling_rate,1/2,'low',4))  ylabel('Temperature [deg C]')  grid on  xlim([yd1 yd2])  %    timeaxis(plot_interval(1,1:3));     datetick('x',12)  linkaxes([p1 p2 p3],'x');  orient tall  eval(['print -depsc ',pd.stage2file,'.2.eps'])   % ------ save data in rodb format ---------  cols      = 'YY:MM:DD:HH:P:T:PFIT';  fort      = '%4.4d  %2.2d  %2.2d  %7.5f   %8.4f %7.4f %8.4f';  time      = gregorian(yd_grid);  data      = [time(:,1:3) hms2h(time(:,4:6)) pr_int(:) t_int(:) pfit(:)];  rodbsave(pd.stage2file,...           ['Latitude:Longitude:Columns:Start_Date:Start_Time:'...            'SerialNumber:Mooring:WaterDepth:Instrdepth:End_Date:End_Time'],...           fort,...           lat,lon,cols,sdate,stime(1:2),...           serialnumber,moor,wd,instrdepth,edate,etime(1:2),...           data);end % for ins = 1 : length(sgI)fclose(fidlog);