function h = hms2h(h,m,s)% a) function h = hms2h(h,m,s) % b) function h = hms2h([h m s]) -- use column vectors for h,m,s % c) function h = hms2h('hh:mm:ss')% convert hour,minute,sec to decimal hour%% kanzowif nargin == 3  h  = h + m./60 + s./3600;elseif nargin == 1  if isstr(h)     h = str2num(h(:,1:2)) + str2num(h(:,4:5))/60 +str2num(h(:,7:8))/3600;  else    h  = h(:,1) + h(:,2)./60 + h(:,3)./3600;  endend  