function map_direct(h,s_grid,NC)% function map_direct(h,s_grid);%% direct instead of scaled mapping% % input:  h       --- figure handle  (i.e.from  [C,h] = contourf(...))%         s_grid  --- contour level grid %         NC      --- number of colors%%    for hh = h(:)'%%hh  %-------------------------------------------------------  %  ColorValue from CData  cc = get(hh,'cdata');   jj = sum( cc >= s_grid  );  jj = jj + ( jj == 0 );  cc = s_grid(jj);  %-------------------------------------------------------  % CDataMapping direct  jj = jj + ( NC - jj )  *  ( jj > NC );  set( hh , 'cdata' , jj , ...            'cdatamapping' , 'direct'  );end