% function month = microcat_month(data)% convert microcat month string into month numberfunction month = microcat_month(data)mon = ['Jan';'Feb';'Mar';'Apr';'May';'Jun';'Jul';'Aug';'Sep';'Oct';'Nov';'Dec'];II = [];for i = 1 :12,  ii = findstr(mon(i,:),data);    month(length(II)+1:length(II)+length(ii)) = i;  II = [II,ii];end  [xxx,I] = sort(II);month   = month(I);  