% Function to convert Jan.Feb ... styles months into number months% For string arrays (designed to use with s42rodb.m routine)% Darren Rayner 09/2005function data = month_convert(data,number_rows)for i=1:number_rows    data(i,1:3)=strrep(data(i,1:3),'Jan','01 ');    data(i,1:3)=strrep(data(i,1:3),'Feb','02 ');    data(i,1:3)=strrep(data(i,1:3),'Mar','03 ');    data(i,1:3)=strrep(data(i,1:3),'Apr','04 ');    data(i,1:3)=strrep(data(i,1:3),'May','05 ');    data(i,1:3)=strrep(data(i,1:3),'Jun','06 ');    data(i,1:3)=strrep(data(i,1:3),'Jul','07 ');    data(i,1:3)=strrep(data(i,1:3),'Aug','08 ');    data(i,1:3)=strrep(data(i,1:3),'Sep','09 ');    data(i,1:3)=strrep(data(i,1:3),'Oct','10 ');    data(i,1:3)=strrep(data(i,1:3),'Nov','11 ');    data(i,1:3)=strrep(data(i,1:3),'Dec','12 ');end  