% function adcp_raw2use_01(moor,'procpath',procpath,'outpath',outpath)%% basic preprocessing for RDI ADCP data% % required inputs: moor - mooring name e.g 'eb3_1_200406'%% optional inputs: procpath - path to proc directory if not using standard%                             paths%                  outpath - path to output processed files to - otherwise%                            outputs to directory function run from%                  plot_interval - matrix of start and end dates for plot%                                  e.g. [2004 02 01 00; 2005 06 01 00]%                                  dates are:- yyyy mm dd hh%% features%      1. eliminate launching and recovery period%      2. save data to rodb file%      3. create data overview sheet%% uses timeaxis.m, auto_filt.m, julian.m, rodbload.m, rodbsave.m% 10/12/09 - DR modified from dvs_raw2use_01function adcp_raw2use_01(moor,varargin)  if nargin==0    help adcp_raw2use_01;    returnenddisplay(' ')display('------------------------------')display('Starting stage 2 adcp_raw2use')display('------------------------------')display(' ')% check for optional argumentsa=strmatch('procpath',varargin,'exact');if a>0    procpath=char(varargin(a+1));else    procpath='/Users/hydrosea5/Desktop/RB1201/rapid/data/moor/proc/';enda=strmatch('outpath',varargin,'exact');if a>0    outpath=char(varargin(a+1))else    outpath = './';enda=strmatch('plot_interval',varargin,'exact');if a>0    plot_interval=varargin(a+1);else    plot_interval=0;endif isunix    [gash, operator]=system('whoami'); else    operator=input('Please enter operator name: ','s');end% -- set path for data inputinpath  = [procpath moor '/'];% --- get moring information from infofile infofile =[procpath moor '/' moor 'info.dat'];[id,sn,z,s_t,s_d,e_t,e_d,lat,lon,wd,mr]  =  rodbload(infofile,'instrument:serialnumber:z:Start_Time:Start_Date:End_Time:End_Date:Latitude:Longitude:WaterDepth:Mooring');vec=find((id>=319) & (id <=328)) % Possible ADCP codes - taken from IMP moorings packagesn=sn(vec);z = z(vec);fid_stat= fopen([outpath moor '_ADCP_stage2.log'],'a')fprintf(fid_stat,['Processing steps taken by ' mfilename ':\n']);fprintf(fid_stat,'  1. eliminate lauching and recovery period\n');fprintf(fid_stat,'  2. save data to rodb file\n');fprintf(fid_stat,'\n Operated by:%s on %s\n',operator,datestr(clock)); fprintf(fid_stat,['        ADCPs in Mooring ',moor,'\n\n\n']);% Determine plot_interval if not input to functionif plot_interval==0    plot_interval = zeros(2,4);    plot_interval(1,1) = s_d(1); plot_interval(1,2) = s_d(2)-1; plot_interval(1,3) = 1; plot_interval(1,4) = 0;    plot_interval(2,1) = e_d(1); plot_interval(2,2) = e_d(2)+1; plot_interval(2,3) = 1; plot_interval(2,4) = 0;    if plot_interval(1,2)==0        plot_interval(1,2)=12; plot_interval(1,1)=plot_interval(1,1)-1;    end    if plot_interval(2,2)==13        plot_interval(2,2)=1; plot_interval(2,1)=plot_interval(2,1)+1;    endend%-----------------------------------------% --- preprocessing loop -------------------% ----------------------------------------jd_s  = julian(s_d(1),s_d(2),s_d(3),s_t(1)+s_t(2)/60);  % start timejd_e  = julian(e_d(1),e_d(2),e_d(3),e_t(1)+e_t(2)/60);  % end time%all_z=[combo_z individual_z];%all_sn=[combo_sn individual_sn];for proc = 1 : length(vec) % loop for multiple instruments on moooring        % first determine how many bin files are to be processed    % trying to do automatically    num_bins=dir([inpath,'adp/*',num2str(sn(proc)) '*.raw'])    num_bins=length(num_bins)    clear press_sensor    disp(['ADCP Serial number: ' num2str(sn(proc))])    press_sensor=input('\nDoes the ADCP have a pressure sensor? y/n:  ','s');    if (strcmp(press_sensor,'y')||strcmp(press_sensor,'Y')||strcmp(press_sensor,'yes')||strcmp(press_sensor,'Yes')||strcmp(press_sensor,'YES'))        press_sensor=1;    end            num_bins    for j=1:num_bins % loop for total number of bins                columns = ['YY:MM:DD:HH:Z:T:U:V:W:HDG:PIT:ROL:CS:CD:BEAM1SS:BEAM2SS:BEAM3SS'...            ':BEAM4SS:BEAM1COR:BEAM2COR:BEAM3COR:BEAM4COR:EV:BEAM1PGP:BEAM2PGP:BEAM3PGP:BEAM4PGP'];                indep  = z(proc);                if j<=9            infile  = [inpath,'adp/',moor,'_',num2str(sn(proc)),'_bin0',num2str(j),'.raw'];        else            infile  = [inpath,'adp/',moor,'_',num2str(sn(proc)),'_bin',num2str(j),'.raw'];        end        if exist(infile,'file')==0            disp(['infile: ' infile ' does not exist.'])        elseif exist(infile,'file')   > 0             if j<=9                rodbfile= [moor,'_',num2str(sn(proc)),'_bin0',num2str(j),'.use'];             else                rodbfile= [moor,'_',num2str(sn(proc)),'_bin',num2str(j),'.use'];             end            outfile = [outpath,rodbfile];            fprintf(fid_stat,'Serialnumber %d \n',sn(proc));            fprintf(fid_stat,'Bin %d \n',j);            fprintf(fid_stat,'Infile %s \n',infile);            fprintf(fid_stat,'Outfile %s \n',outfile);            [YY,MM,DD,HH,z,t,u,v,w,heading,pitch,roll,spd,direction,Amp1,Amp2,Amp3,Amp4,...                Beam1Cor,Beam2Cor,Beam3Cor,Beam4Cor,err,PG1,PG2,PG3,PG4] = ...                rodbload(infile,[columns]);                                    %------------------------------------------             %----- cut off launching and recovery period            %------------------------------------------            disp('cut off launching and recovery period')            jd  = julian(YY,MM,DD,HH);            ii  = find(jd <= jd_e & jd >= jd_s );            YY=YY(ii);MM=MM(ii);DD=DD(ii);HH=HH(ii);            u=u(ii);v=v(ii);w=w(ii);            heading=heading(ii);pitch=pitch(ii);roll=roll(ii);            t=t(ii);            Amp1=Amp1(ii);Amp2=Amp2(ii);Amp3=Amp3(ii);Amp4=Amp4(ii);            Beam1Cor=Beam1Cor(ii);Beam2Cor=Beam2Cor(ii);Beam3Cor=Beam3Cor(ii);Beam4Cor=Beam4Cor(ii);            spd=spd(ii); direction=direction(ii); err=err(ii);            PG1=PG1(ii);PG2=PG2(ii);PG3=PG3(ii);PG4=PG4(ii);            z=z(ii);                        jd  = jd(ii);             cycles     = length(ii);            Start_Date = [YY(1) MM(1) DD(1)];% -- set path for data inputinpath  = [procpath moor '/'];            Start_Time = HH(1);            End_Date = [YY(cycles) MM(cycles) DD(cycles)];            End_Time = HH(cycles);                 %------------------------------------------            %---- fill time gaps  with dummy            %------------------------------------------            disp(' fill time gaps  with dummy')            djd = diff(jd);           % time step              sr  = median(djd);        % sampling interval            ii  = find(djd > 1.5*sr);  % find gaps            gap = round(djd(ii)/sr)-1;            addt= [];             %-----------------------------------------------------            %  write output to logfile ---------------------------            %-----------------------------------------------------            fprintf(fid_stat,'Operation interval: %s  to  %s\n', ...                  datestr(gregorian(jd(1))),datestr(gregorian(jd(end)) ));            fprintf(fid_stat,'\n');            %-----------------------------------              %--- write data to rodb format -----            %-----------------------------------            disp(['writing data to ',outfile])             fort =['%4.4d %2.2d %2.2d  %6.4f  %4.2f  %4.2f %4.1f %4.1f %4.1f  %4.2f '...                    '%4.2f %4.2f  %4.1f  %4.1f  %3.0f %3.0f %3.0f %3.0f   '...                    '%3.0f %3.0f %3.0f %3.0f  %4.1f  %3.0f %3.0f %3.0f %3.0f'];            data = [YY MM DD HH z t u v w heading pitch roll spd direction ...                    Amp1 Amp2 Amp3 Amp4 Beam1Cor Beam2Cor Beam3Cor Beam4Cor ...                    err PG1 PG2 PG3 PG4];                         rodbsave(outfile,...              'Latitude:Longitude:Columns:Start_Date:Start_Time:SerialNumber:Mooring:WaterDepth:Instrdepth:End_Date:End_Time',...               fort,...              lat,lon,columns,Start_Date,Start_Time,sn(proc),mr,wd,indep,End_Date,End_Time,...              data);            %%%%%%%%%% Graphics %%%%%%%%%%%%%%%%            jd0 = julian(-1,1,1,24);            jd1 = julian(plot_interval(1,:))-jd0;            jd2 = julian(plot_interval(2,:))-jd0;             sampling_rate = 1/median(diff(jd));                        STR = ['Zonal Velocity [cm/s] ';                   'Merid. Velocity [cm/s]';                   'Vert. Velocity [cm/s] ';                   'Heading [degrees]     ';                   'Depth [m]             ';                   'Temp. at ADCP [deg C] '];             VAR1 = {'u';'v';'w';'heading';'z';'t'};             plot_vars=6;                        panels=plot_vars;            figure(1);clf            for sub = 1 : plot_vars              eval(['var1 = ',VAR1{sub},';'])              var2=[];              ok = plot_timeseries(jd,var1,var2,sampling_rate,STR(sub,:),sub,[jd1 jd2],'n',panels);            end            subplot(plot_vars,1,1)            title(['ADCP s/n: ',num2str(sn(proc)), ...                 '; Target Depth: ',num2str(indep), ...                 '; Bin ',num2str(j)]);            orient tall            eval(['print -depsc ',outfile,'.eps'])             figure(2);clf            for sub = 1 : plot_vars              eval(['var1 = ',VAR1{sub},';'])              ok = plot_timeseries(jd,var1,var2,sampling_rate,STR(sub,:),sub,[jd1 jd2],'y',panels);            end            subplot(plot_vars,1,1)            title(['ADCP s/n: ',num2str(sn(proc)), ...                   '; Target Depth: ',num2str(indep), ...                 '; Bin ',num2str(j)]);            orient tall            eval(['print -depsc ',outfile,'.filtered.eps'])         end % if exist(infile)==0    end % loop for different bin depthsend  % for proc=1:length(combo_sn)+length(individual_sn) loopend % function  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  function ok = plot_timeseries(jd,var1,var2,sr,str,sub,jdlim,filt,panels)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% plot time series  jd0 = julian(-1,1,1,24);  i1    = find(~isnan(var1) & var1~=0);   if strcmp(filt,'y')    var1  = auto_filt(var1(i1),sr,1/2,'low',4);  else    var1  = var1(i1);  end   if ~isempty(var2)    i2    = find(~isnan(var2) & var2~=0);    if strcmp(filt,'y')      var2  = auto_filt(var2(i2),sr,1/2,'low',4);    else      var2  = var2(i2);    end  end  subplot(panels,1,sub);           plot(jd(i1)-jd0,var1)  hold on  if ~isempty(var2)    plot(jd(i2)-jd0,var2,'r')  end   ylabel(str)  grid on  xlim([jdlim])  datetick('x',12)        ok=1;  end